module top_module (
    input clk,
    input reset,
    input in,
    output disc,
    output flag,
    output err);

reg [2:0] state;
reg [2:0] next_state;

localparam IDLE = 3'b000;
localparam ZERO = 3'b001;
localparam ONE = 3'b010;
localparam SIX_ONES = 3'b011;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= IDLE;
    end else begin
        state <= next_state;
    end
end

always @(*) begin
    case (state)
        IDLE: begin
            if (in) begin
                next_state = ONE;
            end else begin
                next_state = IDLE;
            end
        end
        ONE: begin
            if (in) begin
                next_state = SIX_ONES;
            end else begin
                next_state = ZERO;
            end
        end
        SIX_ONES: begin
            if (in) begin
                next_state = SIX_ONES;
            end else begin
                next_state = ZERO;
            end
        end
        ZERO: begin
            if (in) begin
                next_state = ONE;
            end else begin
                next_state = IDLE;
            end
        end
        default: begin
            next_state = IDLE;
        end
    endcase
end

assign disc = (state == SIX_ONES && !in);
assign flag = (state == SIX_ONES && in);
assign err = (state == SIX_ONES && in);

endmodule

