module top_module (
    input clk,
    input in,
    input reset,
    output done
);

    // State encoding
    localparam IDLE = 2'b00;
    localparam START = 2'b01;
    localparam DATA = 2'b10;
    localparam STOP = 2'b11;

    reg [1:0] state, next_state;
    reg [3:0] bit_counter;
    reg [7:0] data;

    // State register
    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= IDLE;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        case (state)
            IDLE: next_state = (in == 0) ? START : IDLE;
            START: next_state = DATA;
            DATA: begin
                if (bit_counter == 8)
                    next_state = (in == 1) ? STOP : START;
                else
                    next_state = DATA;
            end
            STOP: next_state = (in == 1) ? IDLE : STOP;
            default: next_state = IDLE;
        endcase
    end

    // Output logic
    assign done = (state == STOP);

    // Bit counter
    always @(posedge clk or posedge reset) begin
        if (reset)
            bit_counter <= 0;
        else if (state == START)
            bit_counter <= 0;
        else if (state == DATA)
            bit_counter <= bit_counter + 1;
    end

    // Data capture
    always @(posedge clk) begin
        if (state == DATA && in == 0)
            data <= {data[6:0], 1'b0};
    end

endmodule

module top_module (
    input clk,
    input in,
    input reset,
    output reg [7:0] out_byte,
    output reg done
);

    // State definitions
    reg [2:0] state, next_state;
    parameter IDLE = 3'b000, START = 3'b001, DATA = 3'b010, STOP = 3'b011, END = 3'b100;

    // Counters
    reg [2:0] bit_count;
    reg [2:0] data_count;

    // State machine
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            bit_count <= 0;
            data_count <= 0;
            out_byte <= 8'h00;
            done <= 0;
        end else begin
            state <= next_state;
            case (state)
                IDLE: begin
                    bit_count <= 0;
                    data_count <= 0;
                    done <= 0;
                end
                START: bit_count <= bit_count + 1;
                DATA: begin
                    data_count <= data_count + 1;
                    out_byte <= {in, out_byte[7:1]};
                end
                STOP: begin
                    done <= 1;
                    out_byte <= {in, out_byte[7:1]};
                end
            endcase
        end
    end

    // Next state logic
    always @(*) begin
        case (state)
            IDLE: next_state = (in == 0) ? START : IDLE;
            START: next_state = (bit_count == 1) ? DATA : START;
            DATA: begin
                if (bit_count == 8) next_state = (in == 1) ? STOP : START;
                else next_state = DATA;
            end
            STOP: next_state = END;
            END: next_state = (in == 0) ? START : IDLE;
            default: next_state = IDLE;
        endcase
    end

endmodule

