module top_module(
    input clk,
    input reset,
    input data,
    output reg shift_ena,
    output reg counting,
    input done_counting,
    output reg done,
    input ack );

    // State definitions
    localparam IDLE = 3'b000;
    localparam DETECT_110 = 3'b001;
    localparam DETECT_1 = 3'b010;
    localparam WAIT_FOR_SHIFT = 3'b011;
    localparam WAIT_FOR_COUNT = 3'b100;
    localparam NOTIFY = 3'b101;
    localparam WAIT_FOR_ACK = 3'b110;
    localparam RESET = 3'b111;

    reg [2:0] state, next_state;
    reg [2:0] shift_count;
    reg [3:0] duration;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
        end else begin
            state <= next_state;
        end
    end

    always @(*) begin
        next_state = state;
        shift_ena = 1'b0;
        counting = 1'b0;
        done = 1'b0;

        case (state)
            IDLE: begin
                if (data == 1'b1) begin
                    next_state = DETECT_110;
                end
            end

            DETECT_110: begin
                if (data == 1'b1) begin
                    next_state = DETECT_1;
                end else begin
                    next_state = IDLE;
                end
            end

            DETECT_1: begin
                if (data == 1'b1) begin
                    next_state = WAIT_FOR_SHIFT;
                    shift_ena = 1'b1;
                end else begin
                    next_state = IDLE;
                end
            end

            WAIT_FOR_SHIFT: begin
                if (shift_count == 3'd3) begin
                    next_state = WAIT_FOR_COUNT;
                    shift_count = 3'd0;
                end else begin
                    shift_count = shift_count + 1'b1;
                end
            end

            WAIT_FOR_COUNT: begin
                if (done_counting) begin
                    next_state = NOTIFY;
                end
                counting = 1'b1;
            end

            NOTIFY: begin
                done = 1'b1;
                if (ack) begin
                    next_state = WAIT_FOR_ACK;
                end
            end

            WAIT_FOR_ACK: begin
                next_state = RESET;
            end

            RESET: begin
                next_state = IDLE;
            end

            default: next_state = IDLE;
        endcase
    end

    always @(posedge clk) begin
        if (state == WAIT_FOR_COUNT) begin
            duration <= {duration[2:0], data};
        end
    end

endmodule

