module top_module(
    input clk,
    input reset,
    output [3:1] ena,
    output reg [15:0] q
);

    wire carry_ones, carry_tens, carry_hundreds;

    // Ones digit
    reg [3:0] ones;
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            ones <= 4'b0000;
        end else if (ones == 4'b1001) begin
            ones <= 4'b0000;
        end else begin
            ones <= ones + 1'b1;
        end
    end

    // Tens digit
    reg [3:0] tens;
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            tens <= 4'b0000;
        end else if (ones == 4'b1001) begin
            if (tens == 4'b1001) begin
                tens <= 4'b0000;
            end else begin
                tens <= tens + 1'b1;
            end
        end
    end

    // Hundreds digit
    reg [3:0] hundreds;
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            hundreds <= 4'b0000;
        end else if (ones == 4'b1001 && tens == 4'b1001) begin
            if (hundreds == 4'b1001) begin
                hundreds <= 4'b0000;
            end else begin
                hundreds <= hundreds + 1'b1;
            end
        end
    end

    // Thousands digit (assumed to always be 0-9)
    reg [3:0] thousands;
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            thousands <= 4'b0000;
        end else if (ones == 4'b1001 && tens == 4'b1001 && hundreds == 4'b1001) begin
            if (thousands == 4'b1001) begin
                thousands <= 4'b0000;
            end else begin
                thousands <= thousands + 1'b1;
            end
        end
    end

    // Enable signals for upper digits
    assign ena[1] = (ones == 4'b1001) && (tens != 4'b1001);
    assign ena[2] = (tens == 4'b1001) && (hundreds != 4'b1001);
    assign ena[3] = (hundreds == 4'b1001) && (thousands != 4'b1001);

    // Combine digits
    always @(*) begin
        q = {thousands, hundreds, tens, ones};
    end

endmodule

