module top_module (
    input clk,
    input [7:0] in,
    input reset,
    output [23:0] out_bytes,
    output done
);

    reg [2:0] state, next_state;
    reg [7:0] shift_reg [2:0];
    reg [2:0] byte_counter;

    localparam IDLE = 3'b000;
    localparam WAIT = 3'b001;
    localparam MESSAGE = 3'b010;
    localparam DONE = 3'b011;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            byte_counter <= 0;
        end else begin
            state <= next_state;
            if (state == MESSAGE) begin
                shift_reg[0] <= shift_reg[1];
                shift_reg[1] <= shift_reg[2];
                shift_reg[2] <= in;
                byte_counter <= (byte_counter == 3'b010) ? 3'b000 : byte_counter + 1;
            end
        end
    end

    always @(*) begin
        case (state)
            IDLE: next_state = WAIT;
            WAIT: next_state = (in[7] == 1'b1) ? MESSAGE : WAIT;
            MESSAGE: next_state = (byte_counter == 3'b010) ? DONE : MESSAGE;
            DONE: next_state = WAIT;
            default: next_state = IDLE;
        endcase
    end

    assign done = (state == DONE);

    assign out_bytes[23:16] = shift_reg[2];
    assign out_bytes[15:8] = shift_reg[1];
    assign out_bytes[7:0] = shift_reg[0];

endmodule

