                     (A == 1 && B == 1 && C == 1) ? Q[3] :
                     (A == 1 && B == 1 && C == 0) ? Q[4] :
                     (A == 1 && B == 0 && C == 1) ? Q[5] :
                     (A == 1 && B == 0 && C == 0) ? Q[6] :
                     Q[7];

    always @(posedge clk) begin
        Z <= mux_out;
    end

endmodule

