module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

    reg [2:0] state;
    reg [1:0] counter;

    localparam A = 3'b000;
    localparam B = 3'b001;
    localparam C = 3'b010;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= A;
            counter <= 2'b00;
            z <= 1'b0;
        end else begin
            case (state)
                A: begin
                    if (s) begin
                        state <= B;
                        counter <= 2'b00;
                    end
                end
                B: begin
                    counter <= counter + 1;
                    if (counter < 2'b10) begin
                        if (w) begin
                            counter <= counter + 1;
                        end
                    end else begin
                        if (counter == 2'b10) begin
                            z <= 1'b1;
                        end else begin
                            z <= 1'b0;
                        end
                        counter <= 2'b00;
                        state <= C;
                    end
                end
                C: begin
                    state <= A;
                end
                default: state <= A;
            endcase
        end
    end

endmodule

