module top_module (
    input clk,
    input in,
    input reset,
    output reg out
);

    // State encoding
    localparam A = 1'b0;
    localparam B = 1'b1;

    // State register
    reg state, next_state;

    // Next state logic
    always @(*) begin
        case (state)
            A: next_state = in ? A : B;
            B: next_state = in ? B : A;
            default: next_state = B;
        endcase
    end

    // State register update
    always @(posedge clk) begin
        if (reset)
            state <= B;
        else
            state <= next_state;
    end

    // Output logic
    always @(*) begin
        out = (state == B);
    end

endmodule

