module top_module (
    input clk,
    input reset,
    input [3:1] s,
    output reg fr3,
    output reg fr2,
    output reg fr1,
    output reg dfr
);

    // State encoding
    localparam [3:0] S0 = 4'b0000,
                     S1 = 4'b0001,
