module top_module(
    input clk,
    input reset,
    input data,
    output reg shift_ena,
    output reg counting,
    input done_counting,
    output reg done,
    input ack );

reg [3:0] state;
parameter IDLE = 4'b0000,
          START = 4'b0001,
          SHIFT = 4'b0010,
          COUNT = 4'b0011,
          WAIT = 4'b0100,
          DONE = 4'b0101,
          ACK = 4'b0110;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= IDLE;
        shift_ena <= 0;
        counting <= 0;
        done <= 0;
    end
    else begin
        case (state)
            IDLE: begin
                if (data == 1 && data == 1 && data == 0 && data == 1) begin
                    state <= START;
                end
            end
            START: begin
                state <= SHIFT;
                shift_ena <= 1;
            end
            SHIFT: begin
                state <= COUNT;
                shift_ena <= 0;
            end
            COUNT: begin
                counting <= 1;
                state <= WAIT;
            end
            WAIT: begin
                if (done_counting) begin
                    state <= DONE;
                    counting <= 0;
                end
            end
            DONE: begin
                done <= 1;
                state <= ACK;
            end
            ACK: begin
                if (ack) begin
                    state <= IDLE;
                    done <= 0;
                end
            end
            default: state <= IDLE;
        endcase
    end
end

endmodule

