module top_module(
    input clk,
    input reset,
    input ena,
    output reg pm,
    output reg [7:0] hh,
    output reg [7:0] mm,
    output reg [7:0] ss
);

    // Internal signals
    reg [5:0] hour_count;
    reg [5:0] minute_count;
    reg [5:0] second_count;

    // Clock division
    reg [3:0] clk_div_counter;
    wire clk_div;

    // Counter logic
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            hour_count <= 0;
            minute_count <= 0;
            second_count <= 0;
            pm <= 0;
        end else if (clk_div) begin
            if (second_count == 59) begin
                second_count <= 0;
                if (minute_count == 59) begin
                    minute_count <= 0;
                    if (hour_count == 11) begin
                        hour_count <= 0;
                        pm <= ~pm;
                    end else begin
                        hour_count <= hour_count + 1;
                    end
                end else begin
                    minute_count <= minute_count + 1;
                end
            end else begin
                second_count <= second_count + 1;
            end
        end
    end

    // BCD conversion
    always @(*) begin
        hh = {1'b0, hour_count};
        mm = {1'b0, minute_count};
        ss = {1'b0, second_count};
    end

    // Clock division logic
    always @(posedge clk) begin
        if (clk_div_counter == 15) begin
            clk_div_counter <= 0;
        end else begin
            clk_div_counter <= clk_div_counter + 1;
        end
    end

    assign clk_div = (clk_div_counter == 15) & ena;

endmodule

