    input d,
    input done_counting,
    input ack,
    input [9:0] state,    // 10-bit one-hot current state
    output B3_next,
    output S_next,
    output S1_next,
    output Count_next,
    output Wait_next,
    output done,
    output counting,
    output shift_ena
 );

    // Next-state logic equations
    assign B3_next = state[8];
    assign S_next = (d == 0) ? 1'b1 : 
                    (state[1] & d == 1) ? 1'b1 : 
                    (state[2] & d == 1) ? 1'b1 : 
                    (state[3] & d == 0) ? 1'b1 : 
                    1'b0;
    assign S1_next = (d == 1) ? 1'b1 : 
                     (state[3] & d == 0) ? 1'b1 : 
                     (state[6] & d == 1) ? 1'b1 : 
                     (state[7] & d == 1) ? 1'b1 : 
