    assign Count_next = (state[7] & ~done_counting) ? 1'b1 :
                        (state[9] & done_counting) ? 1'b1 :
                        1'b0;
    assign Wait_next = (state[9] & ack == 1) ? 1'b1 : 
                       1'b0;

    // Output logic equations
    assign done = state[9];
    assign counting = state[7];
    assign shift_ena = state[8];

endmodule

