module top_module(
    input clk,
    input reset,
    input data,
    output start_shifting);

    reg [1:0] state;
    localparam IDLE = 2'b00;
    localparam STATE_1 = 2'b01;
    localparam STATE_11 = 2'b10;
    localparam STATE_110 = 2'b11;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
        end else begin
            case (state)
                IDLE: begin
                    if (data) begin
                        state <= STATE_1;
                    end else begin
                        state <= IDLE;
                    end
                end
                STATE_1: begin
                    if (data) begin
                        state <= STATE_11;
                    end else begin
                        state <= IDLE;
                    end
                end
                STATE_11: begin
                    if (~data) begin
                        state <= STATE_110;
                    end else begin
                        state <= IDLE;
                    end
                end
                STATE_110: begin
                    state <= STATE_110;
                end
                default: begin
                    state <= IDLE;
                end
            endcase
        end
    end

    assign start_shifting = (state == STATE_110);

endmodule

