module gshare_branch_predictor (
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken,
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    localparam TABLE_SIZE = 128;
    localparam INDEX_WIDTH = 7;
    localparam COUNTER_WIDTH = 2;

    reg [INDEX_WIDTH-1:0] global_history;
    reg [COUNTER_WIDTH-1:0] table [TABLE_SIZE-1:0];

    wire [INDEX_WIDTH-1:0] predict_index;
    wire [COUNTER_WIDTH-1:0] predict_counter;
    wire [INDEX_WIDTH-1:0] train_index;

    assign predict_index = predict_pc ^ global_history;
    assign predict_counter = table[predict_index];
    assign predict_taken = predict_counter[COUNTER_WIDTH-1];
    assign predict_history = global_history;

    assign train_index = train_pc ^ global_history;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            global_history <= 0;
            for (int i = 0; i < TABLE_SIZE; i++) begin
                table[i] <= 0;
            end
        end else begin
            if (predict_valid) begin
                if (train_valid && train_mispredicted) begin
                    if (predict_index == train_index) begin
                        table[predict_index] <= (train_taken) ? 2'b11 : 2'b00;
                    end else begin
                        global_history <= {global_history[5:0], predict_taken};
                    end
                end else begin
                    global_history <= {global_history[5:0], predict_taken};
                end
            end

            if (train_valid) begin
                if (train_mispredicted) begin
                    global_history <= {global_history[5:0], train_taken};
                end else begin
                    case (table[train_index])
                        2'b00: table[train_index] <= (train_taken) ? 2'b01 : 2'b00;
                        2'b01: table[train_index] <= 2'b10;
                        2'b10: table[train_index] <= (train_taken) ? 2'b11 : 2'b10;
                        2'b11: table[train_index] <= 2'b11;
                    endcase
                end
            end
        end
    end

endmodule

