module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    reg walk_left_reg, walk_right_reg;
    reg aaah_reg, digging_reg;
    reg [1:0] state, next_state;
    reg [4:0] fall_counter;

    localparam WALK_LEFT = 2'b00, WALK_RIGHT = 2'b01, FALL = 2'b10, DIG = 2'b11;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= WALK_LEFT;
            fall_counter <= 5'b0;
        end else begin
            state <= next_state;
            if (!ground) begin
                fall_counter <= fall_counter + 1;
            end else begin
                fall_counter <= 5'b0;
            end
        end
    end

    always @(*) begin
        next_state = state;
        walk_left_reg = walk_left_reg;
        walk_right_reg = walk_right_reg;
        aaah_reg = aaah_reg;
        digging_reg = digging_reg;

        case (state)
            WALK_LEFT: begin
                walk_left_reg = 1'b1;
                walk_right_reg = 1'b0;
                if (!ground) begin
                    next_state = FALL;
                end else if (bump_left) begin
                    next_state = WALK_RIGHT;
                end
            end
            WALK_RIGHT: begin
                walk_left_reg = 1'b0;
                walk_right_reg = 1'b1;
                if (!ground) begin
                    next_state = FALL;
                end else if (bump_right) begin
                    next_state = WALK_LEFT;
                end
            end
            FALL: begin
                aaah_reg = 1'b1;
                digging_reg = 1'b0;
                if (ground) begin
                    next_state = walk_left ? WALK_LEFT : WALK_RIGHT;
                    aaah_reg = 1'b0;
                end else if (dig && ground) begin
                    next_state = DIG;
                end
            end
            DIG: begin
                digging_reg = 1'b1;
                walk_left_reg = walk_left_reg;
                walk_right_reg = walk_right_reg;
                aaah_reg = 1'b0;
                if (fall_counter > 5'b01010) begin // 20 cycles
                    next_state = FALL;
                    digging_reg = 1'b0;
                    walk_left_reg = 1'b0;
                    walk_right_reg = 1'b0;
                end else if (!ground) begin
                    next_state = FALL;
                    digging_reg = 1'b0;
                end
            end
        endcase
    end

    assign walk_left = walk_left_reg;
    assign walk_right = walk_right_reg;
    assign aaah = aaah_reg;
    assign digging = digging_reg;

endmodule

