module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // State encoding
    localparam WALK_LEFT = 2'b00;
    localparam WALK_RIGHT = 2'b01;
    localparam FALL = 2'b10;
    localparam DIG = 2'b11;

    reg [1:0] state, next_state;

    // State register
    always @(posedge clk or posedge areset) begin
        if (areset)
            state <= WALK_LEFT;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        case (state)
            WALK_LEFT: begin
                if (bump_left)
                    next_state = WALK_RIGHT;
                else if (!ground)
                    next_state = FALL;
                else
                    next_state = WALK_LEFT;
            end
            WALK_RIGHT: begin
                if (bump_right)
                    next_state = WALK_LEFT;
                else if (!ground)
                    next_state = FALL;
                else
                    next_state = WALK_RIGHT;
            end
            FALL: begin
                if (ground)
                    next_state = WALK_LEFT;
                else if (dig)
                    next_state = DIG;
                else
                    next_state = FALL;
            end
            DIG: begin
                if (!ground)
                    next_state = FALL;
                else if (ground && (bump_left || bump_right))
                    next_state = state;
                else if (ground)
                    next_state = WALK_LEFT;
                else
                    next_state = DIG;
            end
            default: next_state = WALK_LEFT;
        endcase
    end

    // Output logic
    assign walk_left = (state == WALK_LEFT);
    assign walk_right = (state == WALK_RIGHT);
    assign aaah = (state == FALL);
    assign digging = (state == DIG);

endmodule

module top_module(
    input clk,
    input reset,
    input ena,
    output reg pm,
    output reg [7:0] hh,
    output reg [7:0] mm,
    output reg [7:0] ss);

    always @(posedge clk) begin
        if (reset) begin
            pm <= 0;
            hh <= 12;
            mm <= 0;
            ss <= 0;
        end
        else if (ena) begin
            if (ss == 59) begin
                ss <= 0;
                if (mm == 59) begin
                    mm <= 0;
                    if (hh == 12) begin
                        pm <= ~pm;
                        hh <= 1;
                    end
                    else if (hh == 11) begin
                        hh <= 12;
                    end
                    else begin
                        hh <= hh + 1;
                    end
                end
                else begin
                    mm <= mm + 1;
                end
            end
            else begin
                ss <= ss + 1;
            end
        end
    end

endmodule

