    input in,
    output out);

    // Internal signals
    reg [1:0] state;
    reg [3:0] counter;
    reg [7:0] data;
    reg valid;

    // FSM states
    localparam IDLE = 2'b00;
    localparam WAIT = 2'b01;
    localparam PROCESS = 2'b10;
    localparam DONE = 2'b11;

    // Counter threshold
    localparam THRESHOLD = 8;

    // Counter reset value
    localparam RESET_VALUE = 0;

    // FSM logic
    always @(posedge in) begin
        case (state)
            IDLE: begin
                counter <= RESET_VALUE;
                data <= 0;
                valid <= 0;
                state <= WAIT;
            end
            WAIT: begin
                counter <= counter + 1;
                data <= 0;
                if (counter == THRESHOLD) begin
                    state <= PROCESS;
                end
            end
            PROCESS: begin
                data <= {data[6:0], in};
                state <= DONE;
            end
            DONE: begin
                valid <= 1;
                state <= IDLE;
            end
        endcase
    end

    // Output logic
    assign out = (state == DONE) ? valid : 0;

