module top_module (
    input clk,
    input reset,
    input w,
    output z
);

    // state variables
    reg [2:0] state, next_state;

    // output variable
    reg z_reg;

    // state definitions
    localparam A = 3'b000;
    localparam B = 3'b001;
    localparam C = 3'b010;
    localparam D = 3'b011;
    localparam E = 3'b100;
    localparam F = 3'b101;

    // state transition logic
    always @(state, w)
        case (state)
            A: next_state = w ? A : B;
            B: next_state = w ? B : C;
            C: next_state = w ? D : E;
            D: next_state = w ? A : F;
            E: next_state = w ? D : E;
            F: next_state = w ? D : C;
            default: next_state = A;
        endcase

    // state update logic
    always @(posedge clk)
        if (reset)
            state <= A;
        else
            state <= next_state;

    // output logic
    always @(*)
        z_reg = (state == E) || (state == F);

    // assign the output signal
    assign z = z_reg;

endmodule

