    input clk,
    input resetn,
    input [3:1] r,
    output [3:1] g
);

reg [1:0] state;
reg [1:0] next;

always @(posedge clk) begin
    if (!resetn) begin
        state <= 2'b00;
    end else begin
        state <= next;
    end
end

