    case (state)
        2'b00: begin
            if (r[1]) next = 2'b01;
            else if (r[2]) next = 2'b10;
            else if (r[3]) next = 2'b11;
            else next = 2'b00;
        end
        2'b01: begin
            if (r[1]) next = 2'b01;
            else next = 2'b00;
        end
        2'b10: begin
            if (r[2]) next = 2'b10;
            else next = 2'b00;
        end
        2'b11: begin
            if (r[3]) next = 2'b11;
            else next = 2'b00;
        end
    endcase
end

assign g = (state == 2'b01) ? 3'b001 :
           (state == 2'b10) ? 3'b010 :
           (state == 2'b11) ? 3'b100 :
           3'b000;

