always @(posedge clk) begin
    if (~resetn)
        state <= 3'b001;
    else
        state <= next;
end

always @(*) begin
    case (state)
        3'b001: begin // A
            if (r[1])
                next = 3'b010;
            else if (r[2])
                next = 3'b011;
            else if (r[3])
                next = 3'b100;
            else
                next = 3'b001;
        end
        3'b010: begin // B
            if (r[1])
                next = 3'b010;
            else
                next = 3'b001;
        end
        3'b011: begin // C
            if (r[2])
                next = 3'b011;
            else
                next = 3'b001;
        end
        3'b100: begin // D
            if (r[3])
                next = 3'b100;
            else
                next = 3'b001;
        end
    endcase
end

assign g[1] = (state == 3'b010);
assign g[2] = (state == 3'b011);
assign g[3] = (state == 3'b100);

endmodule

