module top_module (
    input clk,
    input [7:0] in,
    input reset,
    output [23:0] out_bytes,
    output done
);

reg [23:0] register;
reg [1:0] state;

localparam BYTE1 = 2'b00;
localparam BYTE2 = 2'b01;
localparam BYTE3 = 2'b10;
localparam DONE = 2'b11;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= BYTE1;
        register <= 0;
    end
    else begin
        case (state)
            BYTE1: begin
                if (in[2]) begin
                    state <= BYTE2;
                end
                else begin
                    state <= BYTE1;
                end
                register <= {register[15:0], in};
            end
            BYTE2: begin
                state <= BYTE3;
                register <= {register[15:0], in};
            end
            BYTE3: begin
                state <= DONE;
                register <= {register[15:0], in};
            end
            DONE: begin
                state <= BYTE1;
            end
        endcase
    end
end

assign done = (state == DONE);
assign out_bytes = (done) ? register : 24'b0;

endmodule

