module top_module(
    input d,
    input done_counting,
    input ack,
    input [9:0] state,    // 10-bit one-hot current state
    output reg B3_next,
    output reg S_next,
    output reg S1_next,
    output reg Count_next,
    output reg Wait_next,
    output reg done,
    output reg counting,
    output reg shift_ena
 );

    // State definitions
    localparam S      = 10'b0000000001;
    localparam S1     = 10'b0000000010;
    localparam S11    = 10'b0000000100;
    localparam S110   = 10'b0000001000;
    localparam B0     = 10'b0000010000;
    localparam B1     = 10'b0000100000;
    localparam B2     = 10'b0001000000;
    localparam B3     = 10'b0010000000;
    localparam Count  = 10'b0100000000;
    localparam Wait   = 10'b1000000000;

    // Next state logic
    always @(*) begin
        // Default values
        B3_next = 0;
        S_next = 0;
        S1_next = 0;
        Count_next = 0;
        Wait_next = 0;

        case (state)
            S: begin
                if (d == 0) S_next = 1;
                else S1_next = 1;
            end
            S1: begin
                if (d == 0) S_next = 1;
                else S11_next = 1;
            end
            S11: begin
                if (d == 0) S_next = 1;
                else S110_next = 1;
            end
            S110: begin
                if (d == 0) S_next = 1;
                else B0_next = 1;
            end
            B0: begin
                B1_next = 1;
            end
            B1: begin
                B2_next = 1;
            end
            B2: begin
                B3_next = 1;
            end
            B3: begin
                B3_next = 1;
            end
            Count: begin
                if (done_counting == 0) Count_next = 1;
            end
            Wait: begin
                if (ack == 0) Wait_next = 1;
            end
        endcase
    end

    // Output logic
    always @(*) begin
        done = (state == Wait);
        counting = (state == Count);
        shift_ena = (state == B3) || (state == B2) || (state == B1) || (state == B0);
    end

    // B3_next logic
    always @(*) begin
        if (state == B2) B3_next = 1;
    end

endmodule

