    end

    // State register update
    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= B0;
        else
            state <= next_state;
    end

    // Output logic
    always @(*) begin
        shift_ena = (state == B0) || (state == B1) || (state == B2) || (state == B3);
