    input reset,
    input data,
    output start_shifting);

    enum {S, S1, S11, S110, Done} state, next_state;

    // State register
    always_ff @(posedge clk or posedge reset) begin
        if (reset)
            state <= S;
        else
            state <= next_state;
    end

    // Next state logic
    always_comb begin
        case (state)
            S: if (data) next_state = S1; else next_state = S;
            S1: if (data) next_state = S11; else next_state = S;
            S11: if (data) next_state = S11; else next_state = S110;
            S110: if (data) next_state = Done; else next_state = S;
            Done: next_state = Done;
            default: next_state = S;
        endcase
    end

    // Output logic
    assign start_shifting = (state == Done) ? 1'b1 : 1'b0;

endmodule

