module top_module(
    input wire clk,
    input wire reset,
    output reg [7:0] data_out,
    output reg valid
);

    // State encoding
    enum {IDLE, WAIT, PROCESS, DONE} state, next_state;

    // State register
    reg [31:0] counter;

    // State transitions
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            counter <= 0;
            data_out <= 8'b0;
            valid <= 0;
        end else begin
            state <= next_state;
            case (state)
                IDLE: begin
                    counter <= 0;
                    data_out <= 8'b0;
                    valid <= 0;
                end
                WAIT: begin
                    if (counter == 31) begin
                        next_state <= PROCESS;
                    end else begin
                        next_state <= WAIT;
                    end
                end
                PROCESS: begin
                    data_out <= 8'b10101010; // Example data
                    next_state <= DONE;
                end
                DONE: begin
                    valid <= 1;
                    next_state <= IDLE;
                end
                default: next_state <= IDLE;
            endcase
        end
    end

    // State transitions
    always @(*) begin
        case (state)
            IDLE: next_state = IDLE;
            WAIT: next_state = WAIT;
            PROCESS: next_state = PROCESS;
            DONE: next_state = DONE;
            default: next_state = IDLE;
        endcase
    end

    // Counter increment
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            counter <= 0;
        end else begin
            case (state)
                IDLE: counter <= 0;
                WAIT: counter <= counter + 1;
                default: counter <= counter;
            endcase
        end
    end
endmodule

