        case (state)
            IDLE: next_state = WAIT;
            WAIT: next_state = (counter == 10) ? PROCESS : WAIT;
            PROCESS: next_state = DONE;
            DONE: next_state = IDLE;
            default: next_state = IDLE;
        endcase
    end

