    input wire reset,
    output reg [7:0] data_out,
    output reg valid
);

    reg [1:0] state, next_state;
    reg [3:0] counter;

    localparam IDLE = 2'b00;
    localparam WAIT = 2'b01;
    localparam PROCESS = 2'b10;
    localparam DONE = 2'b11;

