    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= IDLE;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        case (state)
            IDLE: next_state = WAIT;
            WAIT: next_state = (counter == 4'hF) ? PROCESS : WAIT;
            PROCESS: next_state = DONE;
            DEFAULT: next_state = IDLE;
        endcase
    end

    // Counter logic
    always @(posedge clk or posedge reset) begin
        if (reset)
            counter <= 4'h0;
        else if (state == WAIT)
            counter <= counter + 1'b1;
        else
            counter <= 4'h0;
    end

    // Output logic
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            data_out <= 8'h00;
            valid <= 1'b0;
        end else begin
            case (state)
                IDLE: begin
                    data_out <= 8'h00;
                    valid <= 1'b0;
