    output Wait_next,
    output done,
    output counting,
    output shift_ena
);
    wire S, S1, S11, S110, B0, B1, B2, B3, Count, Wait;

    // Extract current state bits
    assign S = state[0];
    assign S1 = state[1];
    assign S11 = state[2];
    assign S110 = state[3];
    assign B0 = state[4];
    assign B1 = state[5];
    assign B2 = state[6];
    assign B3 = state[7];
    assign Count = state[8];
    assign Wait = state[9];

    // Output signals
    assign B3_next = B2;
    assign S_next = (S & ~d) | (S1 & ~d) | (S110 & ~d) | (Wait & ack);
    assign S1_next = S & d;
    assign Count_next = B3 | (Count & ~done_counting);
    assign Wait_next = (Count & done_counting) | (Wait & ~ack);
    assign done = Wait;
    assign counting = Count;
    assign shift_ena = B3 | B2 | B1 | B0;

endmodule
