module top_module (
    input clk,
    input in,
    input areset,
    output out
);

    // Define the states
    parameter A = 2'b00;
    parameter B = 2'b01;

    // Define the state register
    reg [1:0] state_reg;

    // Initialize the state register
    initial begin
        state_reg <= B;
    end

    // Update the state register on the rising edge of the clock or reset
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state_reg <= B;
        end else begin
            case (state_reg)
                A: begin
                    if (in) begin
                        state_reg <= A;
                    end else begin
                        state_reg <= B;
                    end
                end
                B: begin
                    if (in) begin
                        state_reg <= B;
                    end else begin
                        state_reg <= A;
                    end
                end
            endcase
        end
    end

    // Output the state
    assign out = (state_reg == B);

endmodule

