module top_module(
    input clk,
    input reset,
    output reg [31:0] data_out,
    output reg valid
);

    // Define the states of the FSM
    parameter IDLE = 2'b00;
    parameter WAIT = 2'b01;
    parameter PROCESS = 2'b10;
    parameter DONE = 2'b11;
    reg [1:0] state = IDLE;
    reg [31:0] counter = 0;

    // Define the threshold for the counter
    parameter THRESHOLD = 10;

    // Define the data to be processed by the module
    reg [31:0] data = 0;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            counter <= 0;
            data_out <= 0;
            valid <= 0;
        end else begin
            case (state)
                IDLE: begin
                    counter <= 0;
                    data_out <= 0;
                    valid <= 0;
                    if (counter < THRESHOLD) begin
                        state <= WAIT;
                    end
                end
                WAIT: begin
                    counter <= counter + 1;
                    if (counter >= THRESHOLD) begin
                        state <= PROCESS;
                    end
                end
                PROCESS: begin
                    data_out <= data;
                    valid <= 1;
                    state <= DONE;
                end
                DONE: begin
                    state <= IDLE;
                end
            endcase
        end
    end

    // Define the data to be processed by the module
    always @(posedge clk) begin
        if (reset) begin
            data <= 0;
        end else begin
            if (state == PROCESS) begin
                data <= $random;
            end
        end
    end

endmodule

