module top_module (
    input clk,
    input j,
    input k,
    input areset,
    output out
);

    // Define the states
    parameter A = 2'b00;
    parameter B = 2'b01;

    // Define the current state and next state
    reg [1:0] state, next_state;

    // Combinational always block for determining next state
    always @(*) begin
        if (state == A) begin
            if (j) begin
                next_state = B;
            end
            else begin
                next_state = A;
            end
        end
        else begin
            if (k) begin
                next_state = A;
            end
            else begin
                next_state = B;
            end
        end
    end

    // Clocked always block for updating state
    always @(posedge clk) begin
        if (areset) begin
            state <= A;
        end
        else begin
            state <= next_state;
        end
    end

    // Output the current state
    assign out = (state == B) ? 1'b1 : 1'b0;

endmodule

