module top_module(
    input in,
    output out);

    // Define the states of the FSM
    parameter IDLE = 2'b00;
    parameter WAIT = 2'b01;
    parameter PROCESS = 2'b10;
    parameter DONE = 2'b11;

    // Define the threshold for the counter
    parameter THRESHOLD = 4;

    // Define the internal signals
    reg [1:0] state;
    reg [3:0] counter;
    reg [7:0] data_out;
    reg valid_out;

    // Initialize the internal signals
    initial begin
        state = IDLE;
        counter = 0;
        data_out = 0;
        valid_out = 0;
    end

    // Define the FSM
    always @(posedge in) begin
        case(state)
            IDLE: begin
                counter <= 0;
                data_out <= 0;
                valid_out <= 0;
                if (in == 1) begin
                    state <= WAIT;
                end
            end
            WAIT: begin
                counter <= counter + 1;
                if (counter >= THRESHOLD) begin
                    state <= PROCESS;
                end
            end
            PROCESS: begin
                data_out <= in;
                valid_out <= 1;
                state <= DONE;
            end
            DONE: begin
                valid_out <= 0;
                if (in == 0) begin
                    state <= IDLE;
                end
            end
        endcase
    end

    // Output the data and valid signals
    assign out = data_out;
    assign valid_out = valid_out;

endmodule

