module top_module(
    input clk,
    input reset,
    input data,
    output start_shifting);

    parameter S = 3'b000;
    parameter S1 = 3'b001;
    parameter S11 = 3'b010;
    parameter S110 = 3'b011;
    parameter Done = 3'b100;

    reg [2:0] state;
    assign start_shifting = (state == Done);

    always @(posedge clk, posedge reset) begin
        if (reset) begin
            state <= S;
