module top_module (
    input clk,
    input j,
    input k,
    input areset,
    output out
);

    // Define the two states
    parameter A = 2'b00;
    parameter B = 2'b01;

    // Define the current and next states
    reg [1:0] current_state, next_state;

    // Define the output
    assign out = (current_state == B);

    // Combinational always block to determine the next state
    always @(*) begin
        if (current_state == A) begin
            if (j) begin
                next_state = B;
            end else begin
                next_state = A;
            end
        end else begin
            if (k) begin
                next_state = A;
            end else begin
                next_state = B;
            end
        end
    end

    // Clocked always block to update the state
    always @(posedge clk) begin
        if (areset) begin
            current_state <= A;
        end else begin
            current_state <= next_state;
        end
    end

endmodule

