module top_module (
    input clk,
    input j,
    input k,
    input reset,
    output out
);

    // Define the states
    parameter A = 2'b00;
    parameter B = 2'b01;

    // Define the state register and initialize to state A
    reg [1:0] state = A;

    // Define the next state logic
    always @ (posedge clk, posedge reset) begin
        if (reset) begin
            state <= A;
        end else begin
            case (state)
                A: begin
                    if (j) begin
                        state <= B;
                    end
                end
                B: begin
                    if (k) begin
                        state <= A;
                    end
                end
            endcase
        end
    end

    // Define the output logic
    assign out = (state == B);

endmodule

