module top_module(
    input in,
    output out);

    reg [1:0] state;
    reg [7:0] counter;
    reg [7:0] data_out;
    reg valid_out;

    parameter IDLE = 2'b00;
    parameter WAIT = 2'b01;
    parameter PROCESS = 2'b10;
    parameter DONE = 2'b11;

    always @(posedge in or posedge in) begin
        if (in) begin
            state <= IDLE;
            counter <= 0;
            data_out <= 0;
            valid_out <= 0;
        end
    end

    always @(posedge in) begin
        case (state)
            IDLE: begin
                counter <= 0;
                data_out <= 0;
                valid_out <= 0;
                if (in) begin
                    state <= WAIT;
                end
            end
            WAIT: begin
                counter <= counter + 1;
                data_out <= 0;
                valid_out <= 0;
                if (counter == 10) begin
                    state <= PROCESS;
                end
            end
            PROCESS: begin
                data_out <= in;
                valid_out <= 1;
                state <= DONE;
            end
            DONE: begin
                data_out <= 0;
                valid_out <= 0;
                if (in) begin
                    state <= IDLE;
                end
            end
        endcase
    end

    assign out = data_out;

endmodule

