module top_module(
    input clk,
    input rst,
    output reg data_out,
    output reg valid_out,
    output reg [3:0] state
);

// Declare state and counter
reg [3:0] state_reg, state_next;
reg [3:0] counter_reg, counter_next;

// Define states
parameter IDLE = 4'b0000;
parameter WAIT = 4'b0001;
parameter PROCESS = 4'b0010;
parameter DONE = 4'b0011;

// Define counter threshold
parameter THRESHOLD = 4'b1000;

// Define state transition logic
always @(posedge clk, posedge rst) begin
    if (rst) begin
        state_reg <= IDLE;
        counter_reg <= 0;
        data_out <= 0;
        valid_out <= 0;
    end else begin
        state_reg <= state_next;
        counter_reg <= counter_next;
        data_out <= 0;
        valid_out <= 0;
    end
end

// Define state transition and output logic
always @(*) begin
    case (state_reg)
        IDLE: begin
            counter_next = 0;
            if (counter_reg == THRESHOLD) begin
                state_next = WAIT;
            end else begin
                state_next = IDLE;
            end
        end
        WAIT: begin
            counter_next = counter_reg + 1;
            if (counter_reg == THRESHOLD) begin
                state_next = PROCESS;
                data_out <= 1;
                valid_out <= 1;
            end else begin
                state_next = WAIT;
            end
        end
        PROCESS: begin
            counter_next = counter_reg + 1;
            if (counter_reg == THRESHOLD) begin
                state_next = DONE;
                valid_out <= 1;
            end else begin
                state_next = PROCESS;
            end
        end
        DONE: begin
            counter_next = counter_reg + 1;
            if (counter_reg == THRESHOLD) begin
                state_next = IDLE;
            end else begin
                state_next = DONE;
            end
        end
    endcase
end

// Define output logic
always @(posedge clk, posedge rst) begin
    if (rst) begin
        data_out <= 0;
        valid_out <= 0;
    end else begin
        data_out <= in;
        valid_out <= (state_reg == PROCESS);
    end
end

// Assign state and counter outputs
assign state = state_reg;
assign counter = counter_reg;

endmodule

