module top_module (
    input clk,
    input reset,
    input w,
    output z
);

    parameter A = 2'b00;
    parameter B = 2'b01;
    parameter C = 2'b10;
    parameter D = 2'b11;
    parameter E = 3'b000;
    parameter F = 3'b001;

    reg [1:0] state, next_state;
    reg [2:0] current_state, next_current_state;

    always @(posedge clk) begin
        if (reset) begin
            state <= A;
            current_state <= E;
        end
        else begin
            state <= next_state;
            current_state <= next_current_state;
        end
    end

    always @(*) begin
        case (state)
            A: begin
                if (w == 1'b0) begin
                    next_state = B;
                end
                else begin
                    next_state = A;
                end
            end
            B: begin
                if (w == 1'b1) begin
                    next_state = D;
                end
                else begin
                    next_state = C;
                end
            end
            C: begin
                if (w == 1'b1) begin
                    next_state = D;
                end
                else begin
                    next_state = E;
                end
            end
            D: begin
                if (w == 1'b1) begin
                    next_state = A;
                end
                else begin
                    next_state = F;
                end
            end
            E: begin
                next_state = D;
            end
            F: begin
                next_state = C;
            end
        endcase

        case (current_state)
            E: begin
                next_current_state = F;
            end
            F: begin
                next_current_state = D;
            end
        endcase
    end

    assign z = (current_state == E || current_state == F) ? 1'b1 : 1'b0;

